@import url("https://fonts.googleapis.com/css?family=Audiowide");
* {
  box-sizing: border-box;
  list-style: none; }

html {
  font-size: 10px; }

body {
  position: relative;
  background-color: #25292e;
  color: #e0e0e0;
  font-family: 'Audiowide', cursive; }

h1 {
  margin: 50px auto;
  text-align: center;
  font-size: 2.4rem;
  color: #00ff9f;
  text-shadow: 3px 3px 3px #bd00ff; }

.wrap {
  width: 450px;
  margin: 0 auto; }

.board {
  margin: 50px auto; }

.row {
  display: flex; }
  .row .cell .grid {
    width: 30px;
    height: 30px;
    border-radius: 50%;
    border: 1px solid rgba(255, 255, 255, 0.1);
    cursor: pointer; }
    .row .cell .grid:hover {
      background: rgba(255, 255, 255, 0.1); }
  .row .cell.green .grid {
    background: #00ff9f;
    box-shadow: 2px 2px 2px #bd00ff;
    transition: all;
    transition-duration: 0.3s;
    animation: scaleAnimation 0.5s ease 1 both; }
  .row .cell.purple .grid {
    background: #d600ff;
    box-shadow: 2px 2px 2px #001eff;
    transition: all;
    transition-duration: 0.3s;
    animation: scaleAnimation 0.5s ease-in-out 1 both; }

.game-info {
  font-size: 1.4rem; }

.modal {
  width: 300px;
  height: 180px;
  background: rgba(0, 0, 0, 0.5);
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  border-radius: 15px;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  display: none; }
  .modal .winner-wrap {
    font-size: 1.4rem;
    font-weight: bold; }
  .modal button {
    margin-top: 30px; }
  .modal #winner {
    font-size: 1.7rem; }
    .modal #winner.purple {
      color: #d600ff; }
    .modal #winner.green {
      color: #00ff9f; }

@keyframes scaleAnimation {
  0% {
    transform: scale(1); }
  50% {
    transform: scale(1.2); }
  100% {
    transform: scale(0.9); } }
